        function rgbaToArray(rgba) {
            var array = rgba.replace(/[^\d,.]/g, '').split(',');
            for (var i = 0; i < array.length; i++)
                array[i] = parseInt(array[i]);
            array[3] = parseInt(array[3] * 255);
            return array;
        }

        function rgbToArray(rgb) {
            if (rgb || rgb == "")
                return [255,255,255];
            var array = rgb.replace(/[^\d,.]/g, '').split(',');
            for (var i = 0; i < array.length; i++)
                array[i] = parseInt(array[i]);
            return array;
        }

