                                function distance(lat1, lon1, lat2, lon2,
                                    unit) { // unit: 'K' for Kilometers
                                    if ((lat1 == lat2) && (lon1 == lon2)) {
                                        return 0;
                                    } else {
                                        var radlat1 = Math.PI * lat1 / 180;
                                        var radlat2 = Math.PI * lat2 / 180;
                                        var theta = lon1 - lon2;
                                        var radtheta = Math.PI * theta / 180;
                                        var dist = Math.sin(radlat1) * Math.sin(radlat2) + Math.cos(
                                            radlat1) * Math.cos(radlat2) * Math.cos(radtheta);
                                        if (dist > 1) {
                                            dist = 1;
                                        }
                                        dist = Math.acos(dist);
                                        dist = dist * 180 / Math.PI;
                                        dist = dist * 60 * 1.1515;
                                        if (unit == "K") {
                                            dist = dist * 1.609344
                                        }
                                        if (unit == "N") {
                                            dist = dist * 0.8684
                                        }
                                        return dist;
                                    }
                                }

                                function getRadius() {
                                    var radius;
                                    var currentZoom = fullscreendefaultMapRef.getZoom();
                                    if (estimateRadiusFlag && estimatedRadius) {
                                        metresPerPixel = 40075016.686 * Math.abs(Math.cos(
                                            fullscreendefaultMapRef.getCenter().lat * Math.PI /
                                            180)) / Math.pow(2, currentZoom + 8);
                                        radius = ((estimatedRadius * 1000) / metresPerPixel) / 50;
                                        if (radius > 1000) {

                                        } else if (radius > 1) {
                                            if (currentZoom < prevZoom) {
                                                prevZoom = currentZoom;
                                                return radius / 1.2;
                                            } else {
                                                prevZoom = currentZoom;
                                                return radius / 1.2;
                                            }
                                        } else {
                                            prevZoom = currentZoom;
                                            return 1;
                                        }
                                    }
                                    if (prevZoom == null) {
                                        prevZoom = widgetParameters.zoom;
                                    }
                                    if (currentZoom === 7) {
                                        radius = 1;
                                    } else if (currentZoom === 8) {
                                        radius = 1;
                                    } else if (currentZoom === 9) {
                                        radius = 1;
                                    } else if (currentZoom === 10) {
                                        if (currentZoom > prevZoom) {
                                            radius = 2;
                                        } else {
                                            radius = 1;
                                        }
                                    } else if (currentZoom === 11) {
                                        if (currentZoom > prevZoom) {
                                            radius = 3.5;
                                        } else {
                                            radius = 2;
                                        }
                                    } else if (currentZoom === 12) {
                                        if (currentZoom > prevZoom) {
                                            radius = 10;
                                        } else {
                                            radius = 3.5;
                                        }
                                    } else if (currentZoom === 13) {
                                        if (currentZoom > prevZoom) {
                                            radius = 16;
                                        } else {
                                            radius = 10;
                                        }
                                    } else if (currentZoom === 14) {
                                        if (currentZoom > prevZoom) {
                                            radius = 31;
                                        } else {
                                            radius = 16;
                                        }
                                    } else if (currentZoom === 15) {
                                        if (currentZoom > prevZoom) {
                                            radius = 60;
                                        } else {
                                            radius = 31;
                                        }
                                    } else if (currentZoom === 16) {
                                        if (currentZoom > prevZoom) {
                                            radius = 80;
                                        } else {
                                            radius = 60;
                                        }
                                    } else if (currentZoom === 17) {
