            function prevOdPeriod() {
                let actual_date = new Date((dates[current_page].split(" "))[0]);
                let max_difference;
                if (animationPeriod === "week") {
                    let week_day = actual_date.getDay();
                    let checkPrevWeek = false;
                    let temp_page = current_page;
                    temp_page--;
                    //il valore assegnato a max_difference serve per andare indietro fra le date fino al lunedÃ¬ della settimana precedente
                    if (week_day === 0) {
                        max_difference = 13;
                    } else {
                        max_difference = week_day + 6;
                    }
                    while (!checkPrevWeek) {
                        current_page--;
                        if (current_page >= 0) {
                            let temp_date = new Date((dates[current_page].split(" "))[0]);
                            let difference = dateDiffInDays(temp_date, actual_date);
                            if (difference > max_difference) {
                                checkPrevWeek = true;
                            }
                        } else {
                            checkPrevWeek = true;
                        }
                    }
                    if (current_page >= 0 && current_page === temp_page) {
                        current_page--;
                    }
                    nextOdPage();
                } else {
                    let checkMonth = false;
                    let date = dates[current_page].split("-");
                    let year = date[0];
                    let month = date[1];
                    let temp_page = current_page;
                    temp_page--;
                    //ciclo per arrivare all'inizio del mese della data attuale
                    while (!checkMonth) {
                        current_page--;
                        if (current_page >= 0) {
                            let temp_date = dates[current_page].split("-");
                            let temp_year = temp_date[0];
                            let temp_month = temp_date[1];
                            if (month !== temp_month || year !== temp_year) {
                                checkMonth = true;
                            }
                        } else {
                            checkMonth = true;
                        }
                    }
                    checkMonth = false;
                    month = parseInt(month);
                    if (month !== 1) {
                        month--;
                        if (month < 10) {
                            month = "0" + month.toString();
                        } else {
                            month = month.toString();
                        }
                    } else {
                        month = 12;
                        year = parseInt(year);
                        year--;
                        month = month.toString();
                        year = year.toString();
                    }
                    //ciclo per arrivare all'inizio del mese precedente
                    while (!checkMonth) {
                        current_page--;
                        if (current_page >= 0) {
                            let temp_date = dates[current_page].split("-");
                            let temp_year = temp_date[0];
                            let temp_month = temp_date[1];
                            if (month !== temp_month || year !== temp_year) {
                                checkMonth = true;
                            }
                        } else {
                            checkMonth = true;
                        }
                    }
                    if (current_page >= 0 && current_page === temp_page) {
                        current_page--;
                    }
                    nextOdPage();
                }
            }

            function nextOdPeriod() {
                let actual_date = new Date((dates[current_page].split(" "))[0]);
                let max_difference;
                if (animationPeriod === "week") {
                    let week_day = actual_date.getDay();
                    let checkNextWeek = false;
                    let temp_page = current_page;
                    temp_page++;
                    if (week_day === 0) {
                        max_difference = 1;
                    } else {
                        max_difference = 8 - week_day;
                    }
                    while (!checkNextWeek) {
                        current_page++;
                        if (current_page < dates.length) {
                            let temp_date = new Date((dates[current_page].split(" "))[0]);
                            let difference = dateDiffInDays(actual_date, temp_date);
                            if (difference > max_difference) {
                                checkNextWeek = true;
                            }
                        } else {
                            checkNextWeek = true;
                        }
                    }
                    if (current_page < dates.length && current_page === temp_page) {
                        current_page++;
                    }
                    prevOdPage();
                } else {
                    let checkMonth = false;
                    let date = dates[current_page].split("-");
                    let year = date[0];
                    let month = date[1];
                    while (!checkMonth) {
                        current_page++;
                        if (current_page < dates.length) {
                            let temp_date = dates[current_page].split("-");
                            let temp_year = temp_date[0];
                            let temp_month = temp_date[1];
                            if (month !== temp_month || year !== temp_year) {
                                checkMonth = true;
                            }
                        } else {
                            checkMonth = true;
                        }
                    }
                    if (current_page < dates.length) {
                        current_page++;
                    }
                    prevOdPage();
                }
            }

