                                                if (!jQuery.isEmptyObject(roads[i].data[0])) {
                                                    var value = Number(roads[i].data[t][seg.id].replace(",", "."));
                                                    //console.log(value);
                                                    var green = 0.3;
                                                    var yellow = 0.6;
                                                    var orange = 0.9;
