                                        }
                                    } else {
                                        checkNextWeek = true;
                                    }
                                }
                                if(current_page<dates.length && current_page===temp_page) {
                                    current_page++;
                                }
                                prevOdPage();
                            }else{
                                let checkMonth = false;
                                let date = dates[current_page].split("-");
                                let year = date[0];
                                let month = date[1];
                                while (!checkMonth) {
                                    current_page++;
