                        function load(setView, async) {  /// <<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<<< LOOK HERE
                            // get OD data
                            let dataQuery = "";
                            let polygonQuery = "";
                            let type =  ""; // TODO :: controlla retrocompatibilità
                            
                            if (precision == 'communes') {
                                dataQuery = "get";
                                polygonQuery = "polygon";
                            } else if(precision == 'poi' || precision == 'ace' || precision == 'municipality' || precision == 'province' || precision == 'region') {
                                dataQuery = "get";
                                polygonQuery = "polygon";
                                type = precision;
                            } else {
                                dataQuery = "get_mgrs";
                                polygonQuery = "mgrs_polygon";
                            } 

                            targetPolyID = [];
                            sourcePolyID = [];
                            sourcePolyName = [];
                            
                            $.ajax({
                                url: odUrl + dataQuery,
                                async: async == 'False' ? false : true,
                                type: "get",
                                data: {
                                    latitude: latitude,
                                    longitude: longitude,
                                    precision: precision,
                                    from_date: mapDate,
                                    organization: organization,
                                    inflow: inflow,
                                    od_id: odID,
                                    perc: getPerc
                                },
