                                },
                                success: function(response) {
                                    // remove old layer
                                    if (sourcePolygon) {
                                        map.defaultMapRef.removeLayer(sourcePolygon);
                                    }
                                    //console.log('Source response OK')
                                    // MODIFED CODE 2022-05-05
                                    sourcePolyID = [];
                                    sourcePolyName = []
