                        function getColor(d) {
                            for (i = 0; i < colors.length; i++) {
                                min_value = colors[i][0] == '' ? Number.NEGATIVE_INFINITY : colors[i][0];
                                max_value = colors[i][1] == '' ? Number.POSITIVE_INFINITY : colors[i][1];
                                hex = colors[i][2];
                                if (d > min_value && d <= max_value) {
                                    return hex;
                                }
                            }
                        }

