                    newWSConnect();
                    setTimeout(function () {
                        $.event.trigger({
                            type: type,
                            target: target,
                            passedData: passedData,
                            passedParams: passedParams
                        });
                    }, 2000);
                } else {
                    if (!rowParamsObj.isTrafficHeatmap && type != null) {
                        setTimeout(function () {
